import 'package:flutter/cupertino.dart';class AddPersonServices extends ChangeNotifier {  final TextEditingController name = TextEditingController();  final TextEditingController number = TextEditingController();  final TextEditingController cash = TextEditingController();  void clear() {    name.clear();    number.clear();    cash.clear();    _selectedCreditType = null;    _selectedRadio = null;    notifyListeners();  }  String _selectedCreditType;  String _selectedPersonType;  String get selectedPersonType => _selectedPersonType;  set selectedPersonType(String value) {    _selectedPersonType = value;    notifyListeners();  }  String get selectedCreditType => _selectedCreditType;  set selectedCreditType(String value) {    _selectedCreditType = value;    notifyListeners();  }  int _selectedRadio;  int _index = 0;  bool _submit = false;  bool get submit => _submit;  set submit(bool value) {    _submit = value;    notifyListeners();  }  int get index => _index;  set index(int value) {    _index = value;    notifyListeners();  }  int get selectedRadio => _selectedRadio;  set selectedRadio(int value) {    _selectedRadio = value;    notifyListeners();  }}