import 'package:flutter/cupertino.dart';class AddProductServices extends ChangeNotifier {  final TextEditingController name = TextEditingController();  final TextEditingController description = TextEditingController();  final TextEditingController unit = TextEditingController();  final TextEditingController wholesaleUnit = TextEditingController();  final TextEditingController quantityPerWholesaleUnit = TextEditingController();  final TextEditingController quantityPerChosenUnit = TextEditingController();  final TextEditingController netQuantity = TextEditingController();  final TextEditingController quantityLimit = TextEditingController();  final TextEditingController customerPrice = TextEditingController();  final TextEditingController employeePrice = TextEditingController();  final TextEditingController housePrice = TextEditingController();  final TextEditingController newCategoryName = TextEditingController();  void clear() {    name.clear();    description.clear();    quantityLimit.clear();    unit.clear();    wholesaleUnit.clear();    customerPrice.clear();    employeePrice.clear();    housePrice.clear();    newCategoryName.clear();    _selectedCategory = null;    _selectedRadio = 1;    notifyListeners();  }  bool _addingProduct = false;  bool get addingProduct => _addingProduct;  set addingProduct(bool value) {    _addingProduct = value;    notifyListeners();  }  String _selectedCategory;  String get selectedCategory => _selectedCategory;  set selectedCategory(String value) {    _selectedCategory = value;    notifyListeners();  }  int _selectedRadio = 1;  int _selectedRadioButtonsForSelectingQuantity = 1;  int get selectedRadioButtonsForSelectingQuantity => _selectedRadioButtonsForSelectingQuantity;  set selectedRadioButtonsForSelectingQuantity(int value) {    _selectedRadioButtonsForSelectingQuantity = value;    notifyListeners();  }  int _index = 0;  bool _submit = false;  bool get submit => _submit;  set submit(bool value) {    _submit = value;    notifyListeners();  }  int get index => _index;  set index(int value) {    _index = value;    notifyListeners();  }  int get selectedRadio => _selectedRadio;  set selectedRadio(int value) {    _selectedRadio = value;    notifyListeners();  }  // double netTotalQuantity() {  //   if (selectedRadio == 1) {  //     double _wholesaleQuantity = double.parse(wholesaleQuantity.text);  //     print(_wholesaleQuantity);  //     double _quantityOfWholesaleUnit = double.parse(quantityOfWholesaleUnit.text);  //     print(_quantityOfWholesaleUnit);  //     return (_wholesaleQuantity * _quantityOfWholesaleUnit);  //   } else  //     return 0.0;  // }  String netQuantityOfUnit(String quantityPerWholesaleUnit) {    print(quantityPerWholesaleUnit);    if (selectedRadio == 2) {      return "0.0";    } else {      double dQuantityPerWholesaleUnit = double.parse(quantityPerWholesaleUnit);      //2 => w,s,u      if (selectedRadioButtonsForSelectingQuantity == 2) {        return (double.parse(netQuantity.text) * dQuantityPerWholesaleUnit).toString();      } else        return netQuantity.text;    }  }  String netQuantityOfWholesaleUnit(String quantityPerWholesaleUnit) {    print(quantityPerWholesaleUnit);    if (selectedRadio == 2) {      return "0.0";    } else {      double dQuantityPerWholesaleUnit = double.parse(quantityPerWholesaleUnit);      //1 => unit      if (selectedRadioButtonsForSelectingQuantity == 1) {        return (double.parse(netQuantity.text) / dQuantityPerWholesaleUnit).toString();      } else        return netQuantity.text;    }  }  @override  void notifyListeners() {    // TODO: implement notifyListeners    super.notifyListeners();  }}