import 'package:flutter/cupertino.dart';class QuantityPurchaseServices extends ChangeNotifier {  TextEditingController quantity = TextEditingController();  TextEditingController price = TextEditingController();  int _selectedUnitIndex = 1;  int get selectedUnitIndex => _selectedUnitIndex;  set selectedUnitIndex(int value) {    _selectedUnitIndex = value;    notifyListeners();  } // if "1" it is unit if "2" it is wholesale unit  String _selectedUnitType;  String _selectedCategory;  void clear() {    quantity.clear();    price.clear();    _selectedUnitIndex = 1;    selectedCategory = null;    notifyListeners();  }  String get selectedCategory => _selectedCategory;  set selectedCategory(String value) {    _selectedCategory = value;    notifyListeners();  }  String get selectedUnitType => _selectedUnitType;  set selectedUnitType(String value) {    _selectedUnitType = value;    notifyListeners();  }}