import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:gym_bar/ui/shared/text_styles.dart';import 'package:gym_bar/ui/widgets/form_widgets.dart';import 'package:intl/intl.dart';class ChooseDate extends StatefulWidget {  final Map<String, dynamic> args;  const ChooseDate({Key key, this.args}) : super(key: key);  @override  _ChooseDateState createState() => _ChooseDateState();}class _ChooseDateState extends State<ChooseDate> {  String _chosenDate = DateFormat('yyyy-MM-dd').format(DateTime.now());  //todo: change purchase type using radio buttons.  String purchaseType = 'productBuying';  @override  Widget build(BuildContext context) {    print(widget.args["branchName"]);    return Scaffold(      appBar: AppBar(),      body: Column(        children: [          SizedBox(height: 100),          Text(            'برجاء اختيار اليوم',            style: formTitleStyle,          ),          SizedBox(height: 50),          Container(            height: 200,            child: CupertinoDatePicker(                mode: CupertinoDatePickerMode.date,                initialDateTime: DateTime.now(),                minimumDate: DateTime(1997, 1, 1),                maximumDate: DateTime.now(),                onDateTimeChanged: (DateTime newDateTime) {                  _chosenDate =                      DateFormat('yyyy-MM-dd').format(newDateTime);                }),          ),          SizedBox(height: 50),          formButtonTemplate(              context: context,              text: 'Next',              color: Colors.blue,              onTab: () {                print(_chosenDate);                Map<String, dynamic> args = {                  'branchName': widget.args['branchName'],                  'date': _chosenDate,                  'buying': purchaseType                };                Navigator.pushNamed(context,                    widget.args['isSells'] ? '/sells' : '/purchases',                    arguments: args);              }),        ],      ),    );  }}