//ايميل كلمه سر فرع//اسم رقم نوع صورهimport 'package:flutter/material.dart';import 'package:gym_bar/core/services/add_person_services.dart';import 'package:gym_bar/ui/shared/dimensions.dart';import 'package:gym_bar/ui/widgets/form_widgets.dart';import 'package:image_picker/image_picker.dart';import 'package:provider/provider.dart';import 'dart:io';File file;class EmployeeInfo extends StatelessWidget {  @override  Widget build(BuildContext context) {    FormWidget _formWidget = FormWidget(context: context);    Dimensions _dimensions = Dimensions(context);    AddPersonServices addClientServices = Provider.of<AddPersonServices>(context);    Future getImage(String source) async {      // MyApp().checkPermissions(); file =      await ImagePicker.pickImage(source: ImageSource.gallery);      return file;    }    Widget addPhoto() {      return GestureDetector(          onTap: () => getImage(""),          child: file == null              ? _formWidget.logo(imageContent: Image.asset("assets/images/add.jpg"))              : _formWidget.logo(imageContent: Image.file(file)));    }    Widget _subForm = Container();    Widget raseed(hint) {      return Column(children: <Widget>[        _formWidget.formTextFieldTemplate(          hint: hint,          controller: addClientServices.cash,        ),      ]);    }    Widget dropDownType() {      return Padding(        padding: EdgeInsets.only(left: 10, right: 10),        child: DropdownButtonHideUnderline(          child: DropdownButton<String>(            isExpanded: true,            hint: Text(              "أختر النوع", // style: formLabelsStyle,            ),            value: addClientServices.selectedCreditType,            isDense: true,            onChanged: (value) {              addClientServices.selectedCreditType = value;              if (value == "دائن") {                _subForm = raseed("رصيد الشخض الدائن");              } else if (value == "مدين") {                _subForm = raseed("رصيد الشخض المدين");              } else if (value == "خالص") {                _subForm = Column(                  children: <Widget>[Text("رصيد الشخص خالص (يساوي صفر)")],                );              }            },            items: [              DropdownMenuItem<String>(                value: "دائن",                child: Text(                  "دائن",                ),              ),              DropdownMenuItem<String>(                value: "مدين",                child: Text(                  "مدين",                ),              ),              DropdownMenuItem<String>(                value: "خالص",                child: Text(                  "خالص",                ),              ),            ],          ),        ),      );    }    return Column(      mainAxisAlignment: MainAxisAlignment.center,      children: <Widget>[        addPhoto(),        _formWidget.formTextFieldTemplate(hint: "اسم الشخص", controller: addClientServices.name),        _formWidget.formTextFieldTemplate(            hint: "رقم الشخص", controller: addClientServices.number),        dropDownType(),        _subForm,        Padding(          padding: EdgeInsets.only(left: 10, right: 10),          child: Divider(            thickness: 1,          ),        ),      ],    );  }}