//اسم رقم نوع صورهimport 'package:flutter/material.dart';import 'package:gym_bar/core/services/add_person_services.dart';import 'package:gym_bar/ui/shared/dimensions.dart';import 'package:gym_bar/ui/shared/text_styles.dart';import 'package:gym_bar/ui/widgets/form_widgets.dart';import 'package:image_picker/image_picker.dart';import 'package:provider/provider.dart';import 'dart:io';File file;class PersonChoices extends StatelessWidget {  @override  Widget build(BuildContext context) {    FormWidget _formWidget = FormWidget(context: context);    Dimensions _dimensions = Dimensions(context);    TextStyles _textStyles = TextStyles(context: context);    AddPersonServices addPersonServices = Provider.of<AddPersonServices>(context);    var selectedPersonType = addPersonServices.selectedPersonType;    Future getImage(String source) async {      // MyApp().checkPermissions(); file =      await ImagePicker.pickImage(source: ImageSource.gallery);      return file;    }    Widget addPhoto() {      return GestureDetector(          onTap: () => getImage(""),          child: file == null              ? _formWidget.logo(imageContent: Image.asset("assets/images/add.jpg"))              : _formWidget.logo(imageContent: Image.file(file)));    }    Widget dropDownType() {      return Padding(        padding: EdgeInsets.only(left: 10, right: 10),        child: DropdownButtonHideUnderline(          child: DropdownButton<String>(            isExpanded: true,            hint: Text(              "أختر النوع", // style: formLabelsStyle,            ),            value: addPersonServices.selectedCreditType,            isDense: true,            onChanged: (value) {              addPersonServices.selectedCreditType = value;            },            items: [              DropdownMenuItem<String>(                value: "دائن",                child: Text(                  "دائن",                ),              ),              DropdownMenuItem<String>(                value: "مدين",                child: Text(                  "مدين",                ),              ),              DropdownMenuItem<String>(                value: "خالص",                child: Text(                  "خالص",                ),              ),            ],          ),        ),      );    }    List<Widget> personTypeChoices() {      return [        ChoiceChip(          padding: EdgeInsets.symmetric(              horizontal: _dimensions.widthPercent(5), vertical: _dimensions.heightPercent(0.4)),          labelStyle: _textStyles.chipLabelStyleLight(),          selectedColor: Colors.blue,          backgroundColor: Colors.white,          shape: StadiumBorder(            side: BorderSide(color: Colors.blue),          ),          label: Text("موظف"),          selected: selectedPersonType == "موظف",          onSelected: (_) => addPersonServices.selectedPersonType = "موظف",        ),        SizedBox(width: _dimensions.widthPercent(2)),        ChoiceChip(          padding: EdgeInsets.symmetric(              horizontal: _dimensions.widthPercent(5), vertical: _dimensions.heightPercent(0.4)),          labelStyle: _textStyles.chipLabelStyleLight(),          backgroundColor: Colors.white,          selectedColor: Colors.blue,          shape: StadiumBorder(side: BorderSide(color: Colors.blue)),          label: Text("عميل"),          selected: selectedPersonType == "عميل",          onSelected: (_) => addPersonServices.selectedPersonType = "عميل",        ),      ];    }    return Card(      elevation: 2,      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(20)),      child: Center(        child: Column(          mainAxisAlignment: MainAxisAlignment.spaceEvenly,          children: [            Text("اضف صوره"),            addPhoto(),            Text("اختر نوع الشخص"),            Row(              mainAxisAlignment: MainAxisAlignment.spaceEvenly,              children: personTypeChoices(),            )          ],        ),      ),    );  }}