//اسم رقم نوع صورهimport 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:gym_bar/core/services/add_person_services.dart';import 'package:gym_bar/ui/shared/dimensions.dart';import 'package:gym_bar/ui/shared/text_styles.dart';import 'package:gym_bar/ui/widgets/form_widgets.dart';import 'package:provider/provider.dart';import 'dart:io';File file;class PersonGeneralInfoCard extends StatelessWidget {  final formKey;  const PersonGeneralInfoCard({Key key, this.formKey}) : super(key: key);  @override  Widget build(BuildContext context) {    FormWidget _formWidget = FormWidget(context: context);    Dimensions _dimensions = Dimensions(context);    TextStyles _textStyles = TextStyles(context: context);    AddPersonServices addPersonServices = Provider.of<AddPersonServices>(context);    Widget dropDownType() {      return Padding(        padding: EdgeInsets.only(            left: _dimensions.widthPercent(2), right: _dimensions.widthPercent(2)),        child: DropdownButtonFormField<String>(          decoration: InputDecoration.collapsed(hintText: ""),          isExpanded: true,          hint: Text(            "أختر النوع",            style: _textStyles.formLabelsStyle(),          ),          value: addPersonServices.selectedCreditType,          isDense: true,          onChanged: (value) {            addPersonServices.selectedCreditType = value;          },          items: [            DropdownMenuItem<String>(              value: "دائن",              child: Text(                "دائن",                style: _textStyles.formLabelsStyleBlack(),              ),            ),            DropdownMenuItem<String>(              value: "مدين",              child: Text(                "مدين",                style: _textStyles.formLabelsStyleBlack(),              ),            ),            DropdownMenuItem<String>(              value: "خالص",              child: Text(                "خالص",                style: _textStyles.formLabelsStyleBlack(),              ),            ),          ],          validator: (String value) {            if (value?.isEmpty ?? true) {              return "برجاء ملأ جميع الخانات";            }            return null;          },        ),      );    }    return Card(      elevation: 2,      shape: RoundedRectangleBorder(          borderRadius: BorderRadius.circular(_dimensions.heightPercent(2))),      child: Padding(        padding: EdgeInsets.all(_dimensions.widthPercent(2)),        child: Form(            key: formKey,            child: Column(              mainAxisAlignment: MainAxisAlignment.spaceEvenly,              children: [                // addPhoto(),                _formWidget.formTextFieldTemplate(                  hint: "اسم الشخص",                  controller: addPersonServices.name,                ),                _formWidget.formTextFieldTemplate(                    hint: "رقم الشخص", controller: addPersonServices.number),                dropDownType(),                addPersonServices.selectedCreditType == null                    ? Container()                    : addPersonServices.selectedCreditType == "خالص"                        ? Text(                            "رصيد الشخص خالص (يساوي صفر)",                            style: _textStyles.formLabelsStyleBlack(),                          )                        : _formWidget.formTextFieldTemplate(                            hint: "رصيد الشخض",                            controller: addPersonServices.cash,                            keyboardType: TextInputType.number,                            inputFormatters: [                              FilteringTextInputFormatter.allow(RegExp(r'[0-9]')),                            ],                          ),              ],            )),      ),    );  }}